import React from 'react';
import axios from 'axios';
import {Form, Input, Button} from 'antd';

const initialState={
    username:"",
    password:"",


};

class Deneme extends React.Component {

    state= initialState ;

    handleChange= (e) => {
        this.setState({
           [e.target.name] : e.target.value ,
        })
    };
    handleSubmit =(e) => {
        e.preventDefault();

        console.log(this.state);
        this.setState(initialState);



        axios({
                url: '/api/user',
                method: 'POST',
                data: this.state,

              })
                .then(() => {
                  console.log('user verisi gönderildi');



                })
                .catch(() => {
                  console.log('Internal server error');
                });;


    };

    render(){
        return (
            <div className="Form">
                <Form onSubmit={this.handleSubmit} className="col-md-12 ofset-md-2">
                    <div>
                        <Input type="text" name="firstname" value={this.state.username}  onChange={ this.handleChange }/>
                    </div>
                    <div>
                        <Input type="text" name="lastname" value={this.state.lastname}  onChange={ this.handleChange }/>
                    </div>
                    <div>
                        <Input type="text" name="email" value={this.state.email}  onChange={ this.handleChange }/>
                    </div>
                    <div>
                        <Input type="text" name="password" value={this.state.password}  onChange={ this.handleChange }/>
                    </div>
                    <div>
                        <Button onSubmit={this.handleSubmit}>Submit</Button>
                    </div>
                </Form>
            </div>
        )
    }
}
