import React from 'react';
import axios from 'axios'
import { Modal, Button, Form, Input } from 'antd';
import {UserOutlined} from '@ant-design/icons';




class Login extends React.Component {

    state = { 
      visible: false,
      email:'',
      password:''
    };

  handleChange = ({target}) => {
    const {name, value} = target;
    this.setState({
      [name]:value
      });
  };

  showModal = () => {
    this.setState({
      visible: true,
    });
  };

  handleOk = e => {
    console.log(e);
    this.setState({
      visible: false,
    });
  };

  handleCancel = e => {
    console.log(e);
    this.setState({
      visible: false,
    });
  };

  submit = (event) => {
    event.preventDefault();

    const payload = {
      firstname: this.state.firstname,
      lastname: this.state.lastname,
      email: this.state.email,
      password: this.state.password
    };

    axios({
      url:'http://localhost:8080/save',
      method:'POST',
      data:payload
    })
    .then(() =>{
      console.log('Veri kaydedildi', )
    })
    .catch(() =>{
      console.log('Hata' )
    });;
  };

    render() {
        return (
            <div>
                <Button style={{ background: "#5B2121", borderColor: "#5B2121"}} type="primary" onClick={this.showModal} icon={<UserOutlined />}>
          Giris Yap
                </Button>
        <Modal
          title="Giris Yap"
          visible={this.state.visible}
          onOk={this.handleOk}
          onCancel={this.handleCancel}
        >
          <Form onSubmit={this.submit}>

                <div className="form-group">
                    <label>Email address</label>
                    <Input type="email" name="email" value={this.state.email} className="form-control" placeholder="Enter email" />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <Input type="password" name="password" value={this.state.password} className="form-control" placeholder="Enter password" />
                </div>

                <div className="form-group">
                    <div className="custom-control custom-checkbox">
                        <input type="checkbox" className="custom-control-input" id="customCheck1" />
                        <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                    </div>
                </div>
                <Button onSubmit={this.submit}>Submit</Button>
            </Form>
        </Modal>
            </div>
        )
    }
}

export default Login;
